count = 0content = ""puts Dir.pwdFile.open("lib/IDocument.as") do |file|    content = file.readendcontent.each_line do |line|  args = line.match(/\(.+\)/)  if args    new_args = args.to_s.split(/\(|,|\)/).map do |pair|       type, var = pair.split       "#{var}:#{type}"    end    new_args.delete_at(0)    new_args = "(#{new_args.join(", ")})"    print line.sub(args.to_s, new_args)  else    print line  end  end