count = 0content = ""puts Dir.pwdFile.open("lib/compdoc/IDocument.as") do |file|    content = file.readendcontent.each_line do |line|  args = line.match(/function \w+/)  if args    func, func_name = args.to_s.split    new_name = func_name.clone    new_name[0, 1] = new_name[0, 1].downcase#    puts "#{func_name} => #{new_name}"#    print "#{count += 1}: #{line.sub(func_name, new_name)}"    print line.sub(func_name, new_name)  else#    print "--#{count += 1}: #{line}"    print line  end  end